import{r as a,o as r,a as s,d as c,b as n,w as i,F as d,f as o,e}from"./app.2886e81c.js";import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";const f={},l=o('<p>\u63D2\u4EF6\u4E3A VuePress \u5E26\u6765\u4E86\u589E\u5F3A\u7684 PWA \u652F\u6301<sup class="footnote-ref"><a href="#footnote1">[1]</a><a class="footnote-anchor" id="footnote-ref1"></a></sup>\u3002</p><h2 id="\u5185\u5BB9\u7F13\u5B58\u548C\u66F4\u65B0" tabindex="-1"><a class="header-anchor" href="#\u5185\u5BB9\u7F13\u5B58\u548C\u66F4\u65B0" aria-hidden="true">#</a> \u5185\u5BB9\u7F13\u5B58\u548C\u66F4\u65B0</h2><p>Service Worker <sup class="footnote-ref"><a href="#footnote2">[2]</a><a class="footnote-anchor" id="footnote-ref2"></a></sup> (\u7B80\u79F0 SW) \u4E3B\u8981\u7528\u4E8E\u83B7\u53D6\u5E76\u6258\u7BA1\u7F51\u7AD9\u5185\u5BB9\u3002</p><p>\u672C\u63D2\u4EF6\u4F1A\u81EA\u52A8\u901A\u8FC7 <code>workbox-build</code> \u6CE8\u518C Service Woker\u3002</p><p>\u4E3A\u4E86\u66F4\u597D\u5730\u63A7\u5236 Service Worker \u53EF\u4EE5\u9884\u7F13\u5B58\u7684\u5185\u5BB9\uFF0C\u63D2\u4EF6\u63D0\u4F9B\u4E86\u4E0B\u5217\u8BBE\u7F6E\u3002</p><p>\u5982\u679C\u4F60\u662F\u4E00\u4E2A\u9AD8\u7EA7\u7528\u6237\uFF0C\u4F60\u4E5F\u53EF\u4EE5\u76F4\u63A5\u8BBE\u7F6E <code>generateSwConfig</code> \u6765\u5C06\u9009\u9879\u4F20\u9012\u7ED9 <code>workbox-build</code>\u3002</p><h3 id="\u9ED8\u8BA4\u7F13\u5B58" tabindex="-1"><a class="header-anchor" href="#\u9ED8\u8BA4\u7F13\u5B58" aria-hidden="true">#</a> \u9ED8\u8BA4\u7F13\u5B58</h3><p>\u9ED8\u8BA4\u60C5\u51B5\u4E0B\u63D2\u4EF6\u4F1A\u9884\u7F13\u5B58\u6240\u6709\u4E0E\u7F51\u7AD9\u76F8\u5173\u7684\u6587\u4EF6: <code>**/*.{html,js,css,svg}</code></p><p>\u540C\u65F6\u63D2\u4EF6\u8FD8\u4F1A\u7F13\u5B58\u5B57\u4F53\u6587\u4EF6: <code>**/*.{woff,woff2,eot,ttf,otf}</code>\u3002</p><h3 id="\u7F13\u5B58\u63A7\u5236" tabindex="-1"><a class="header-anchor" href="#\u7F13\u5B58\u63A7\u5236" aria-hidden="true">#</a> \u7F13\u5B58\u63A7\u5236</h3><p>\u57FA\u4E8E\u53EF\u5B89\u88C5\u6027<sup class="footnote-ref"><a href="#footnote3">[3]</a><a class="footnote-anchor" id="footnote-ref3"></a></sup>\u7684\u8981\u6C42\uFF0C\u63D2\u4EF6\u63D0\u4F9B\u4E86\u7F13\u5B58\u63A7\u5236\u7684\u76F8\u5173\u9009\u9879\u3002</p><h4 id="\u56FE\u7247\u7F13\u5B58" tabindex="-1"><a class="header-anchor" href="#\u56FE\u7247\u7F13\u5B58" aria-hidden="true">#</a> \u56FE\u7247\u7F13\u5B58</h4><p>\u4F60\u53EF\u4EE5\u901A\u8FC7\u8BBE\u7F6E <code>cachePic</code> \u9009\u9879\u4E3A <code>true</code> \u6765\u7F13\u5B58\u7AD9\u70B9\u56FE\u7247\u3002</p><p>\u5982\u679C\u60A8\u7684\u7AD9\u70B9\u4F53\u79EF\u4E0D\u5927\uFF0C\u4E14\u914D\u56FE\u5927\u591A\u4E3A\u5173\u952E\u6027\u8BF4\u660E\uFF0C\u5E0C\u671B\u53EF\u4EE5\u5728\u79BB\u7EBF\u6A21\u5F0F\u4E0B\u663E\u793A\uFF0C\u5EFA\u8BAE\u5C06\u6B64\u9879\u8BBE\u7F6E\u4E3A <code>true</code>\u3002</p><div class="custom-container info"><p class="custom-container-title">\u56FE\u7247\u8BC6\u522B</p><p>\u6211\u4EEC\u901A\u8FC7\u6587\u4EF6\u540E\u7F00\u540D\u8BC6\u522B\u56FE\u7247\uFF0C\u4EFB\u4F55\u4EE5 <code>.png</code>, <code>.jpg</code>, <code>.jpeg</code> , <code>.gif</code>, <code>.bmp</code>, <code>.webp</code> \u7ED3\u5C3E\u7684\u6587\u4EF6\u90FD\u4F1A\u89C6\u4E3A\u56FE\u7247\u3002</p></div><h4 id="html-\u7F13\u5B58" tabindex="-1"><a class="header-anchor" href="#html-\u7F13\u5B58" aria-hidden="true">#</a> HTML \u7F13\u5B58</h4><p>\u5F53\u4F60\u7F51\u7AD9\u4F53\u79EF\u8FC7\u5927\u65F6\uFF0C\u4F60\u53EF\u4EE5\u901A\u8FC7\u8BBE\u7F6E <code>cacheHTML</code> \u4E3A <code>false</code> \u6765\u4EC5\u7F13\u5B58\u9664\u4E3B\u9875\u548C 404 \u9519\u8BEF\u9875\u3002</p><div class="custom-container tip"><p class="custom-container-title">\u4E3A\u4EC0\u4E48\u53EF\u4EE5\u79FB\u9664</p><p>\u867D\u7136\u8BF4 VuePress \u4E3A\u6240\u6709\u7684\u9875\u9762\u901A\u8FC7 SSR<sup class="footnote-ref"><a href="#footnote4">[4]</a><a class="footnote-anchor" id="footnote-ref4"></a></sup> \u751F\u6210\u4E86 HTML \u6587\u4EF6\uFF0C\u4F46\u662F\u8FD9\u4E9B\u6587\u4EF6\u4E3B\u8981\u7528\u4E8E SEO<sup class="footnote-ref"><a href="#footnote5">[5]</a><a class="footnote-anchor" id="footnote-ref5"></a></sup>\uFF0C\u5E76\u80FD\u591F\u8BA9\u4F60\u5728\u540E\u7AEF\u4E0D\u505A SPA<sup class="footnote-ref"><a href="#footnote6">[6]</a><a class="footnote-anchor" id="footnote-ref6"></a></sup> \u914D\u7F6E\u7684\u60C5\u51B5\u4E0B\u80FD\u591F\u76F4\u63A5\u8BBF\u95EE\u4EFB\u4F55\u94FE\u63A5\u3002</p><p>VuePress \u672C\u8D28\u4E0A\u662F\u4E00\u4E2A SPA\u3002\u8FD9\u610F\u5473\u7740\u4F60\u53EA\u9700\u8981\u7F13\u5B58\u4E3B\u9875\u5E76\u4ECE\u4E3B\u9875\u8FDB\u5165\u5373\u53EF\u6B63\u5E38\u8BBF\u95EE\u6240\u6709\u9875\u9762\u3002</p><p>\u5F53\u4F60\u7AD9\u70B9\u9875\u9762\u6570\u91CF\u6216\u5185\u5BB9\u5F88\u591A\uFF0C\u5305\u542B HTML \u6587\u4EF6\u540E\u4F53\u79EF\u8FC7\u5927\u65F6\uFF0C\u4F60\u53EF\u4EE5\u8003\u8651\u5C06\u6B64\u9879\u8BBE\u7F6E\u4E3A <code>false</code>\uFF0C\u8FD9\u6837\u53EF\u4EE5\u7F29\u51CF\u5927\u7EA6 40% \u7684\u4F53\u79EF\u3002\u7F3A\u70B9\u662F\u7528\u6237\u5728\u79BB\u7EBF\u73AF\u5883\u4E0B\u53EA\u80FD\u901A\u8FC7\u4E3B\u9875\u8FDB\u5165\u518D\u81EA\u884C\u5BFC\u822A\u5230\u5BF9\u5E94\u9875\u9762\u3002\u76F4\u63A5\u8BBF\u95EE\u67D0\u4E2A\u94FE\u63A5\u4F1A\u51FA\u73B0\u7F51\u9875\u9519\u8BEF\u7684\u63D0\u793A\u3002</p></div><h4 id="\u5927\u5C0F\u63A7\u5236" tabindex="-1"><a class="header-anchor" href="#\u5927\u5C0F\u63A7\u5236" aria-hidden="true">#</a> \u5927\u5C0F\u63A7\u5236</h4><p>\u4E3A\u4E86\u9632\u6B62\u5728\u9884\u7F13\u5B58\u5217\u8868\u4E2D\u5305\u542B\u5927\u6587\u4EF6\uFF0C\u4EFB\u4F55\u5927\u4E8E 2MB \u7684\u6587\u4EF6\u6216\u5927\u4E8E 1MB \u7684\u56FE\u7247\u90FD\u5C06\u88AB\u5220\u9664\u3002</p><p>\u4F60\u53EF\u4EE5\u901A\u8FC7 <code>maxSize</code> \u9009\u9879\u81EA\u5B9A\u4E49\u7F13\u5B58\u7684\u6700\u5927\u6587\u4EF6\u5927\u5C0F (\u5355\u4F4D KB)\uFF0C\u6216\u901A\u8FC7 <code>maxPicSize</code> \u6765\u66F4\u6539\u56FE\u7247\u7684\u5927\u5C0F\u9650\u5236 (\u5355\u4F4D: KB)\u3002</p><h3 id="\u66F4\u65B0\u5F39\u7A97" tabindex="-1"><a class="header-anchor" href="#\u66F4\u65B0\u5F39\u7A97" aria-hidden="true">#</a> \u66F4\u65B0\u5F39\u7A97</h3><p>\u6210\u529F\u4E0B\u8F7D\u65B0\u5185\u5BB9\u540E\uFF0C\u6211\u4EEC\u5C06\u63D0\u4F9B\u66F4\u65B0\u5F39\u51FA\u7A97\u53E3\u3002</p><div class="custom-container tip"><p class="custom-container-title">\u81EA\u5B9A\u4E49\u5F39\u7A97</p><p>\u5982\u679C\u4F60\u5BF9\u9ED8\u8BA4\u7684\u5F39\u7A97\u4E0D\u6EE1\u610F\uFF0C\u4F60\u53EF\u4EE5\u81EA\u884C\u7F16\u5199\u7EC4\u4EF6\u66F4\u6362\u3002\u4F60\u9700\u8981\u5168\u5C40\u6CE8\u518C\u81EA\u5DF1\u7684\u5F39\u7A97\u7EC4\u4EF6\uFF0C\u5E76\u5C06\u7EC4\u4EF6\u7684\u540D\u79F0\u4F20\u9012\u7ED9 <code>popupComponent</code> \u9009\u9879\u3002</p></div><h2 id="\u6E05\u5355\u6587\u4EF6\u751F\u6210" tabindex="-1"><a class="header-anchor" href="#\u6E05\u5355\u6587\u4EF6\u751F\u6210" aria-hidden="true">#</a> \u6E05\u5355\u6587\u4EF6\u751F\u6210</h2><p>\u4E3A\u4E86\u4FDD\u8BC1 PWA \u7684\u53EF\u5B89\u88C5\u6027\uFF0C\u7F51\u7AD9\u9700\u8981\u751F\u6210\u6E05\u5355\u6587\u4EF6\uFF0C\u5E76\u901A\u8FC7 <code>link</code> \u58F0\u660E\u6709\u6548\u7684 manifest \u6E05\u5355\u6587\u4EF6\u5730\u5740<sup class="footnote-ref"><a href="#footnote7">[7]</a><a class="footnote-anchor" id="footnote-ref7"></a></sup>\u3002</p><p>\u63D2\u4EF6\u4F1A\u5728\u8F93\u51FA\u76EE\u5F55\u81EA\u52A8\u4E3A\u4F60\u751F\u6210 Manifest \u6587\u4EF6 <code>manifest.webmanifest</code>\uFF0C\u540C\u65F6\u8FD8\u4F1A\u6DFB\u52A0\u6E05\u5355\u5730\u5740\u58F0\u660E\u5230\u6BCF\u4E00\u4E2A HTML \u7684 <code>&lt;head&gt;</code> \u4E2D\u3002</p><p>\u5982\u679C\u4F60\u5728 <code>.vuepress/public</code> \u4E2D\u5DF2\u6709\u4E00\u4E2A <code>manifest.webmanifest</code> \u6216 <code>manifest.json</code>\uFF0C\u8BE5\u63D2\u4EF6\u5C06\u8BFB\u53D6\u5B83\u5E76\u5408\u5E76\u5230\u6700\u7EC8 manifest \u4E2D\u3002</p><h3 id="\u81EA\u52A8\u751F\u6210" tabindex="-1"><a class="header-anchor" href="#\u81EA\u52A8\u751F\u6210" aria-hidden="true">#</a> \u81EA\u52A8\u751F\u6210</h3><p>\u63D2\u4EF6\u4F1A\u5C3D\u53EF\u80FD\u7684\u901A\u8FC7 VuePress \u63D2\u4EF6\u63A5\u53E3\u7684\u4FE1\u606F\uFF0C\u4E3A manifest \u7684\u5927\u90E8\u5206\u8BBE\u7F6E\u9879\u8BBE\u7F6E fallback\uFF0C\u8FD9\u610F\u5473\u7740\u5373\u4F7F\u4F60\u65E0\u9700\u8BBE\u7F6E manifest \u7684\u5927\u591A\u6570\u5185\u5BB9\u3002</p><p>\u5982\u679C\u672A\u8BBE\u7F6E\u4E0B\u5217\u9009\u9879\uFF0C\u5B83\u4EEC\u4F1A\u6309\u7167\u987A\u5E8F\u4F9D\u6B21\u5C1D\u8BD5\u56DE\u9000\u5230\u4EE5\u4E0B\u9884\u8BBE\u503C\u3002</p><p>| \u9009\u9879 | \u9ED8\u8BA4\u503C | | --------------------------- | ------------------------------------------------------------------------------------------------------ | --- | --------- | | name | <code>siteConfig.title</code> || <code>themeConfig.title</code> || <code>&#39;Site&#39;</code> | | short_name | <code>siteConfig.title</code> || <code>themeConfig.title</code> || <code>&#39;Site&#39;</code> | | description | <code>siteConfig.description</code> || <code>themeConfig.description</code> || <code>&#39;A site built with vuepress-theme-hope&#39;</code> | | lang | <code>siteConfig.locales[&#39;/&#39;].lang</code> || <code>themeConfig.locales[&#39;/&#39;].lang</code> || <code>&quot;en-US&quot;</code> | \\ | <code>&quot;en-US&quot;</code> | | start_url | <code>context.base</code> | | scope | <code>context.base</code> | | display | <code>&quot;standalone&quot;</code> | | theme_color | <code>&quot;#46bd87&quot;</code> | | background_color | <code>&#39;#ffffff&#39;</code> | | orientation | <code>&#39;portrait-primary&#39;</code> | | prefer_related_applications | <code>false</code> |</p><p>\u5B8C\u6574\u7684\u914D\u7F6E\u9879\u8BE6\u89C1 <a href="https://github.com/vuepress-theme-hope/vuepress-theme-hope/blob/main/packages/pwa/src/shared/manifest.d.ts" target="_blank" rel="noopener noreferrer">Manifest \u7C7B\u578B\u5B9A\u4E49\u6587\u4EF6</a></p><h3 id="\u624B\u52A8\u914D\u7F6E" tabindex="-1"><a class="header-anchor" href="#\u624B\u52A8\u914D\u7F6E" aria-hidden="true">#</a> \u624B\u52A8\u914D\u7F6E</h3><p>\u4F60\u53EF\u4EE5\u5728 <code>manifest</code> \u9009\u9879\u4E2D\u624B\u52A8\u6307\u5B9A manifest \u7684\u5404\u9879\u5185\u5BB9\u3002</p><div class="custom-container tip"><p class="custom-container-title">\u4F18\u5148\u7EA7</p><p><code>manifest</code> \u9009\u9879\u7684\u8BBE\u7F6E\u5177\u6709\u6700\u9AD8\u7684\u4F18\u5148\u7EA7\uFF0C\u4E4B\u540E\u662F <code>public</code> \u6587\u4EF6\u5939\u4E0B\u53EF\u80FD\u5B58\u5728\u7684 manifest \u6587\u4EF6\u3002</p></div><p><strong>\u4F60\u81F3\u5C11\u5E94\u8BE5\u901A\u8FC7 <code>manifest.icons</code> \u6216 PWA \u63D2\u4EF6\u4E2D\u7684\u5176\u4ED6\u9009\u9879\u8BBE\u7F6E\u4E00\u4E2A\u6709\u6548\u7684\u56FE\u6807\u3002</strong> \u56E0\u4E3A\u8FD9\u662F\u6211\u4EEC\u6CA1\u6CD5\u9ED8\u8BA4\u751F\u6210\u7684\u3002</p><div class="custom-container warning"><p class="custom-container-title">\u6CE8\u610F</p><p>\u53EF\u5B89\u88C5\u6027<sup class="footnote-ref"><a href="#footnote3">[3:1]</a><a class="footnote-anchor" id="footnote-ref3:1"></a></sup>\u89C4\u8303\u8981\u6C42 manifest \u4E2D\u81F3\u5C11\u58F0\u660E\u4E00\u4E2A\u6709\u6548\u7684\u56FE\u6807\u3002</p><p>\u6240\u4EE5\u5982\u679C\u4F60\u4E0D\u914D\u7F6E <code>manifest.icons</code>\uFF0C\u8BBF\u95EE\u8005\u53EA\u80FD\u4EAB\u53D7\u5230 Service Worker \u7F13\u5B58\u5E26\u6765\u7684\u79BB\u7EBF\u53EF\u8BBF\u95EE\u6027\uFF0C\u800C\u5E76\u4E0D\u80FD\u4F5C\u4E3A PWA \u8FDB\u884C\u5B89\u88C5\u3002</p><p>\u53E6\u5916\u63D2\u4EF6\u5E76\u4E0D\u4F1A\u5904\u7406 manifest \u4E2D\u7684\u4EFB\u4F55\u5185\u5BB9\uFF0C\u800C\u662F\u539F\u6837\u8F93\u51FA\u5B83\u4EEC\u3002\u8FD9\u610F\u5473\u7740\u5982\u679C\u6253\u7B97\u4F60\u90E8\u7F72\u5230\u67D0\u4E2A\u5B50\u76EE\u5F55\uFF0C\u4F60\u5219\u5E94\u81EA\u884C\u6DFB\u52A0 <code>base</code> \u5230 manifest \u4E2D\u7684\u76F8\u5E94 URL\u3002</p></div><h2 id="\u5176\u4ED6\u9009\u9879" tabindex="-1"><a class="header-anchor" href="#\u5176\u4ED6\u9009\u9879" aria-hidden="true">#</a> \u5176\u4ED6\u9009\u9879</h2><p>\u63D2\u4EF6\u8FD8\u63D0\u4F9B\u4E86\u5176\u4ED6 PWA \u76F8\u5173\u9009\u9879\uFF0C\u6BD4\u5982\u5FAE\u8F6F\u78C1\u8D34\u56FE\u6807\u4E0E\u989C\u8272\u8BBE\u7F6E\uFF0C\u82F9\u679C\u56FE\u6807\u7B49\u3002</p>',40),h=e("\u4F60\u53EF\u4EE5\u914C\u60C5\u6839\u636E\u9700\u8981\u8BBE\u7F6E\u5B83\u4EEC\u3002\u8BE6\u7EC6\u7684\u9009\u9879\u8BF7\u89C1 "),u=e("\u914D\u7F6E\u9875"),m=e("\u3002"),g=o('<h2 id="\u76F8\u5173\u9605\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u76F8\u5173\u9605\u8BFB" aria-hidden="true">#</a> \u76F8\u5173\u9605\u8BFB</h2><p>\u66F4\u591A\u5185\u5BB9\uFF0C\u8BF7\u8BE6\u89C1:</p><ul><li><a href="https://web.dev/progressive-web-apps/" target="_blank" rel="noopener noreferrer">Google PWA</a></li><li><a href="https://developer.mozilla.org/zh-CN/docs/Web/Progressive_web_apps" target="_blank" rel="noopener noreferrer">MDN PWA</a></li><li><a href="https://w3c.github.io/manifest/" target="_blank" rel="noopener noreferrer">W3C Manifest \u89C4\u8303</a></li></ul><hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="footnote1" class="footnote-item"><p><strong>PWA \u4ECB\u7ECD</strong></p><p>PWA \u5168\u79F0 Progressive Web app\uFF0C\u5373\u6E10\u8FDB\u5F0F\u7F51\u7EDC\u5E94\u7528\u7A0B\u5E8F\uFF0C\u6807\u51C6\u7531 W3C \u89C4\u5B9A\u3002</p><p>\u5B83\u5141\u8BB8\u7F51\u7AD9\u901A\u8FC7\u652F\u6301\u8BE5\u7279\u6027\u7684\u6D4F\u89C8\u5668\u5C06\u7F51\u7AD9\u4F5C\u4E3A App \u5B89\u88C5\u5728\u5BF9\u5E94\u5E73\u53F0\u4E0A\u3002 <a href="#footnote-ref1" class="footnote-backref">\u21A9\uFE0E</a></p></li><li id="footnote2" class="footnote-item"><p><strong>Service Worker \u7B80\u8981\u4ECB\u7ECD</strong></p><ol><li><p>Service Worker \u4F1A\u5728\u6CE8\u518C\u8FC7\u7A0B\u4E2D\u83B7\u53D6\u6CE8\u518C\u5728\u5176\u4E2D\u7684\u6240\u6709\u6587\u4EF6\u5E76\u7F13\u5B58\u5B83\u4EEC\u3002</p></li><li><p>\u6CE8\u518C\u6210\u529F\u540E\uFF0CService Worker \u6FC0\u6D3B\uFF0C\u5E76\u5F00\u59CB\u4EE3\u7406\u5E76\u63A7\u5236\u4F60\u7684\u5168\u90E8\u8BF7\u6C42\u3002</p></li><li><p>\u6BCF\u5F53\u4F60\u60F3\u8981\u901A\u8FC7\u6D4F\u89C8\u5668\u53D1\u8D77\u8BBF\u95EE\u8BF7\u6C42\u540E\uFF0CService Worker \u5C06\u4F1A\u67E5\u770B\u5176\u662F\u5426\u5B58\u5728\u4E0E\u81EA\u8EAB\u7F13\u5B58\u5217\u8868\u4E2D\uFF0C\u82E5\u5B58\u5728\u5219\u76F4\u63A5\u8FD4\u56DE\u7F13\u5B58\u597D\u7684\u7ED3\u679C\uFF0C\u5426\u5219\u8C03\u7528\u81EA\u8EAB\u7684 fetch \u65B9\u6CD5\u8FDB\u884C\u83B7\u53D6\u3002\u4F60\u53EF\u4EE5\u901A\u8FC7\u81EA\u5B9A\u4E49 fetch \u65B9\u6CD5\uFF0C\u6765\u5B8C\u5168\u63A7\u5236\u7F51\u9875\u5185\u8D44\u6E90\u83B7\u53D6\u8BF7\u6C42\u7684\u7ED3\u679C\uFF0C\u6BD4\u5982\u5728\u79BB\u7EBF\u65F6\u63D0\u4F9B\u4E00\u4E2A fallback \u7684\u7F51\u9875\u3002</p></li><li><p>\u6BCF\u6B21\u7528\u6237\u91CD\u65B0\u6253\u5F00\u7F51\u7AD9\u65F6\uFF0CService Worker \u4F1A\u5411\u81EA\u8EAB\u6CE8\u518C\u65F6\u7684\u5730\u5740\u53D1\u51FA\u6821\u9A8C\u547D\u4EE4\uFF0C\u5982\u679C\u68C0\u6D4B\u5230\u65B0\u7248\u672C\u7684 Service Woker\uFF0C\u5219\u4F1A\u66F4\u65B0\u81EA\u8EAB\uFF0C\u5E76\u5F00\u59CB\u7F13\u5B58\u6CE8\u518C\u5728\u65B0 Service Worker \u4E2D\u7684\u8D44\u6E90\u5217\u8868\u3002\u6210\u529F\u83B7\u53D6\u5185\u5BB9\u66F4\u65B0\u540E\uFF0CService Worker \u5C06\u4F1A\u89E6\u53D1 <code>update</code> \u4E8B\u4EF6\u3002\u53EF\u4EE5\u901A\u8FC7\u6B64\u4E8B\u4EF6\u63D0\u793A\u7528\u6237\uFF0C\u6BD4\u5982\u5C06\u5728\u53F3\u4E0B\u89D2\u663E\u793A\u4E00\u4E2A\u5F39\u51FA\u7A97\u53E3\uFF0C\u63D0\u793A\u7528\u6237\u65B0\u5185\u5BB9\u53EF\u7528\u5E76\u5141\u8BB8\u7528\u6237\u89E6\u53D1\u66F4\u65B0\u3002</p></li></ol><a href="#footnote-ref2" class="footnote-backref">\u21A9\uFE0E</a></li><li id="footnote3" class="footnote-item"><p><strong>\u53EF\u5B89\u88C5\u6027</strong></p><p>\u60F3\u8981\u8BA9\u7F51\u7AD9\u53EF\u4EE5\u6CE8\u518C\u4E3A PWA\uFF0C\u7F51\u7AD9\u9700\u8981\u81EA\u884C\u6210\u529F\u6CE8\u518C\u6709\u6548\u7684 Service Worker\uFF0C\u540C\u65F6\u6DFB\u52A0\u5408\u6CD5\u7684 manifest \u6E05\u5355\u6587\u4EF6\u5E76\u5728\u7F51\u7AD9\u4E2D\u58F0\u660E\u5B83\u3002</p><p>\u5404\u5E73\u53F0\u6216\u6D4F\u89C8\u5668\u5BF9 Service Worker \u7F13\u5B58\u7684\u5927\u5C0F\u6709\u8981\u6C42\uFF0C\u5F53 Service Worker \u7F13\u5B58\u7684\u6587\u4EF6\u4F53\u79EF\u8FC7\u5927\u540E\uFF0C\u8BE5\u7F51\u7AD9\u5C06\u4F1A\u88AB\u6807\u8BB0\u4E3A\u4E0D\u53EF\u5B89\u88C5\u3002\u5BF9\u4E8E Safari \u8FD9\u4E2A\u9608\u503C\u662F 50 MB\uFF0C\u5C11\u6570\u6D4F\u89C8\u5668\u4F1A\u8BBE\u7F6E\u66F4\u5C11\u6216\u66F4\u591A\u7684\u503C (30MB, 70MB, 80MB)\uFF0C\u6700\u5927\u7684 Chrome \u4E5F\u5C06\u9608\u503C\u6807\u8BC6\u5728 100 MB\u3002</p><p>\u53E6\u5916\uFF0C\u6E05\u5355\u6587\u4EF6\u5E94\u81F3\u5C11\u5305\u542B <code>name</code>(\u6216 <code>short_name</code>) <code>icons</code> <code>start_url</code>\u3002</p><p>\u4ECE Chrome 93 \u8D77 Service Woker \u5FC5\u987B\u542B\u6709\u6709\u6548\u7684\u63A7\u5236\u79BB\u7EBF\u8BF7\u6C42\u7684 fetch \u4E8B\u4EF6 <a href="#footnote-ref3" class="footnote-backref">\u21A9\uFE0E</a> <a href="#footnote-ref3:1" class="footnote-backref">\u21A9\uFE0E</a></p></li><li id="footnote4" class="footnote-item"><p><strong>SSR</strong>: <strong>S</strong>erver <strong>S</strong>ide <strong>R</strong>endering\uFF0C\u670D\u52A1\u7AEF\u6E32\u67D3 <a href="#footnote-ref4" class="footnote-backref">\u21A9\uFE0E</a></p></li><li id="footnote5" class="footnote-item"><p><strong>SEO</strong>: <strong>S</strong>earch <strong>E</strong>ngine <strong>O</strong>ptimization\uFF0C\u641C\u7D22\u5F15\u64CE\u589E\u5F3A\uFF0C</p><p>\u8BE6\u89C1 <a href="https://mrhope.site/code/website/html/definition/seo/" target="_blank" rel="noopener noreferrer">SEO \u4ECB\u7ECD</a> <a href="#footnote-ref5" class="footnote-backref">\u21A9\uFE0E</a></p></li><li id="footnote6" class="footnote-item"><p><strong>SPA</strong>: <strong>S</strong>ingle <strong>P</strong>age <strong>A</strong>pplication, \u5355\u9875\u5E94\u7528</p><p>\u5927\u591A\u53EA\u6709\u4E3B\u9875\uFF0C\u5E76\u4F7F\u7528 history mode \u5904\u7406\u8DEF\u7531\uFF0C\u800C\u4E0D\u662F\u771F\u7684\u5728\u9875\u9762\u4E4B\u95F4\u5BFC\u822A\u3002 <a href="#footnote-ref6" class="footnote-backref">\u21A9\uFE0E</a></p></li><li id="footnote7" class="footnote-item"><p><strong>\u6E05\u5355\u6587\u4EF6</strong></p><p>\u6E05\u5355\u6587\u4EF6\u4F7F\u7528 JSON \u683C\u5F0F\uFF0C\u8D1F\u8D23\u58F0\u660E PWA \u5404\u9879\u4FE1\u606F\uFF0C\u5982\u540D\u79F0\u3001\u63CF\u8FF0\u3001\u56FE\u6807\u3001\u5FEB\u6377\u52A8\u4F5C\u7B49\u3002</p><p>\u4E3A\u4E86\u4F7F\u4F60\u7684\u7AD9\u70B9\u80FD\u591F\u88AB\u6CE8\u518C\u4E3A PWA\uFF0C\u4F60\u9700\u8981\u6EE1\u8DB3 manifest \u57FA\u672C\u7684\u89C4\u8303\uFF0C\u624D\u80FD\u4F7F\u6D4F\u89C8\u5668\u8BA4\u4E3A\u8BE5\u7F51\u7AD9\u4E3A\u4E00\u4E2A\u53EF\u5B89\u88C5\u7684 PWA \u5E76\u5141\u8BB8\u7528\u6237\u5B89\u88C5\u5B83\u3002</p><div class="custom-container info"><p class="custom-container-title">\u76F8\u5173\u4FE1\u606F</p><p>Manifest \u7684\u6807\u51C6\u4E0E\u89C4\u8303\uFF0C\u8BF7\u8BE6\u89C1 <a href="https://w3c.github.io/manifest/" target="_blank" rel="noopener noreferrer">W3C Manifest</a></p></div><a href="#footnote-ref7" class="footnote-backref">\u21A9\uFE0E</a></li></ol></section>',5);function b(v,k){const t=a("RouterLink");return r(),s(d,null,[l,c("p",null,[h,n(t,{to:"/zh/config.html"},{default:i(()=>[u]),_:1}),m]),g],64)}var W=p(f,[["render",b]]);export{W as default};
